contracts/AgriSensorData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title AgriSensorData
 * @dev Immutable storage of IoT sensor data with role-based access control.
 * Features:
 * - Sensor readings stored permanently on-chain
 * - Role-based permissions for devices, farmers, researchers
 * - Batch submissions for IoT gateways
 * - Query functions for historical data
 * - Crop lifecycle tracking
 * - Supply chain traceability
 */
contract AgriSensorData is AccessControl, Pausable {
    // Role definitions
    bytes32 public constant DEVICE_ROLE = keccak256("DEVICE_ROLE");
    bytes32 public constant FARMER_ROLE = keccak256("FARMER_ROLE");
    bytes32 public constant SUPPLY_CHAIN_ROLE = keccak256("SUPPLY_CHAIN_ROLE");
    bytes32 public constant RESEARCHER_ROLE = keccak256("RESEARCHER_ROLE");

    // Security parameters
    uint256 public constant SUBMISSION_COOLDOWN = 60; // seconds between submissions
    uint256 public constant MAX_BATCH_SIZE = 100; // max readings per batch

    // Custom errors
    error UnauthorizedDevice();
    error InvalidSensorData();
    error ArrayLengthMismatch();
    error DuplicateDataHash();
    error ReadingNotFound();

    // Sensor reading structure
    struct SensorReading {
        uint256 timestamp;
        address deviceId;
        uint256 farmId;
        int16 temperature;      // in 0.1°C (e.g., 255 = 25.5°C)
        uint16 soilMoisture;    // percentage 0-1000 (0.1% precision)
        uint16 humidity;        // percentage 0-1000 (0.1% precision)
        bytes32 dataHash;       // for verification
    }

    // Crop lifecycle event structure
    struct CropEvent {
        uint256 farmId;
        string eventType;       // "SEED", "FERTILIZE", "IRRIGATE", "HARVEST"
        uint256 timestamp;
        string notes;
        bytes32 linkedDataHash; // optional link to sensor data
    }

    // Supply chain stage structure
    struct SupplyChainStage {
        uint256 productId;
        string stage;           // "FARM", "TRANSPORT", "STORAGE", "RETAIL"
        uint256 timestamp;
        address recorder;
        string location;
        bytes32 linkedDataHash; // link to sensor data (e.g., transport temp)
    }

    // Storage
    SensorReading[] public readings;
    CropEvent[] public cropEvents;
    SupplyChainStage[] public supplyChainStages;

    mapping(bytes32 => bool) public dataHashes;             // prevent duplicate submissions
    mapping(uint256 => uint256[]) public farmToReadings;    // farmId => reading indices
    mapping(uint256 => uint256[]) public farmToCropEvents;  // farmId => crop event indices
    mapping(uint256 => uint256[]) public productToStages;   // productId => supply chain indices
    mapping(address => uint256) public lastSubmissionTime;  // device => last submission timestamp

    // Events
    event SensorDataSubmitted(
        uint256 indexed readingId,
        address indexed deviceId,
        uint256 indexed farmId,
        uint256 timestamp
    );

    event CropEventRecorded(
        uint256 indexed eventId,
        uint256 indexed farmId,
        string eventType,
        uint256 timestamp
    );

    event SupplyChainStageRecorded(
        uint256 indexed stageId,
        uint256 indexed productId,
        string stage,
        uint256 timestamp
    );

    event AnomalyDetected(
        uint256 indexed readingId,
        address indexed deviceId,
        string anomalyType
    );

    /**
     * @dev Constructor grants admin role to deployer
     */
    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(DEVICE_ROLE, msg.sender);
        _grantRole(FARMER_ROLE, msg.sender);
        _grantRole(RESEARCHER_ROLE, msg.sender);
        _grantRole(SUPPLY_CHAIN_ROLE, msg.sender);
    }

    /**
     * @dev Submit a single sensor reading
     * @param farmId The farm identifier
     * @param temperature Temperature in 0.1°C
     * @param soilMoisture Soil moisture 0-1000 (0.1% precision)
     * @param humidity Humidity 0-1000 (0.1% precision)
     */
    function submitSensorData(
        uint256 farmId,
        int16 temperature,
        uint16 soilMoisture,
        uint16 humidity
    ) external onlyRole(DEVICE_ROLE) whenNotPaused {
        // Rate limiting check
        if (block.timestamp < lastSubmissionTime[msg.sender] + SUBMISSION_COOLDOWN) {
            revert("Cooldown period active");
        }
        lastSubmissionTime[msg.sender] = block.timestamp;
        // Validate data ranges
        if (soilMoisture > 1000 || humidity > 1000) revert InvalidSensorData();

        // Compute hash for verification
        bytes32 hash = keccak256(abi.encodePacked(
            block.timestamp,
            msg.sender,
            farmId,
            temperature,
            soilMoisture,
            humidity
        ));

        // Check for duplicates
        if (dataHashes[hash]) revert DuplicateDataHash();
        dataHashes[hash] = true;

        // Store reading
        uint256 readingId = readings.length;
        readings.push(SensorReading({
            timestamp: block.timestamp,
            deviceId: msg.sender,
            farmId: farmId,
            temperature: temperature,
            soilMoisture: soilMoisture,
            humidity: humidity,
            dataHash: hash
        }));

        farmToReadings[farmId].push(readingId);

        emit SensorDataSubmitted(readingId, msg.sender, farmId, block.timestamp);

        // Basic anomaly detection
        _checkAnomalies(readingId, temperature, soilMoisture, humidity);
    }

    /**
     * @dev Submit batch sensor readings (for IoT gateways)
     * @param farmIds Array of farm identifiers
     * @param temperatures Array of temperatures
     * @param moistures Array of soil moisture values
     * @param humidities Array of humidity values
     */
    function submitBatch(
        uint256[] calldata farmIds,
        int16[] calldata temperatures,
        uint16[] calldata moistures,
        uint16[] calldata humidities
    ) external onlyRole(DEVICE_ROLE) whenNotPaused {
        uint256 len = farmIds.length;

        // Batch size limit
        if (len > MAX_BATCH_SIZE) {
            revert("Batch size exceeds maximum");
        }

        if (len != temperatures.length || len != moistures.length || len != humidities.length) {
            revert ArrayLengthMismatch();
        }

        // Rate limiting check
        if (block.timestamp < lastSubmissionTime[msg.sender] + SUBMISSION_COOLDOWN) {
            revert("Cooldown period active");
        }
        lastSubmissionTime[msg.sender] = block.timestamp;

        for (uint256 i = 0; i < len; ) {
            if (moistures[i] > 1000 || humidities[i] > 1000) revert InvalidSensorData();

            bytes32 hash = keccak256(abi.encodePacked(
                block.timestamp,
                msg.sender,
                farmIds[i],
                temperatures[i],
                moistures[i],
                humidities[i],
                i // include index to prevent duplicate hashes in same batch
            ));

            if (dataHashes[hash]) revert DuplicateDataHash();
            dataHashes[hash] = true;

            uint256 readingId = readings.length;
            readings.push(SensorReading({
                timestamp: block.timestamp,
                deviceId: msg.sender,
                farmId: farmIds[i],
                temperature: temperatures[i],
                soilMoisture: moistures[i],
                humidity: humidities[i],
                dataHash: hash
            }));

            farmToReadings[farmIds[i]].push(readingId);

            emit SensorDataSubmitted(readingId, msg.sender, farmIds[i], block.timestamp);

            _checkAnomalies(readingId, temperatures[i], moistures[i], humidities[i]);

            unchecked { ++i; }
        }
    }

    /**
     * @dev Record a crop lifecycle event
     * @param farmId The farm identifier
     * @param eventType Type of event (SEED, FERTILIZE, etc.)
     * @param notes Additional notes
     * @param linkedDataHash Optional hash linking to sensor data
     */
    function recordCropEvent(
        uint256 farmId,
        string calldata eventType,
        string calldata notes,
        bytes32 linkedDataHash
    ) external onlyRole(FARMER_ROLE) whenNotPaused {
        uint256 eventId = cropEvents.length;
        cropEvents.push(CropEvent({
            farmId: farmId,
            eventType: eventType,
            timestamp: block.timestamp,
            notes: notes,
            linkedDataHash: linkedDataHash
        }));

        farmToCropEvents[farmId].push(eventId);

        emit CropEventRecorded(eventId, farmId, eventType, block.timestamp);
    }

    /**
     * @dev Record a supply chain stage
     * @param productId Unique product identifier
     * @param stage Stage name (FARM, TRANSPORT, STORAGE, RETAIL)
     * @param location Location description
     * @param linkedDataHash Optional hash linking to sensor data
     */
    function recordSupplyChainStage(
        uint256 productId,
        string calldata stage,
        string calldata location,
        bytes32 linkedDataHash
    ) external onlyRole(SUPPLY_CHAIN_ROLE) whenNotPaused {
        uint256 stageId = supplyChainStages.length;
        supplyChainStages.push(SupplyChainStage({
            productId: productId,
            stage: stage,
            timestamp: block.timestamp,
            recorder: msg.sender,
            location: location,
            linkedDataHash: linkedDataHash
        }));

        productToStages[productId].push(stageId);

        emit SupplyChainStageRecorded(stageId, productId, stage, block.timestamp);
    }

    /**
     * @dev Get all sensor readings for a farm
     * @param farmId The farm identifier
     * @return Array of reading indices
     */
    function getReadingsByFarm(uint256 farmId) external view returns (uint256[] memory) {
        return farmToReadings[farmId];
    }

    /**
     * @dev Get crop events for a farm
     * @param farmId The farm identifier
     * @return Array of event indices
     */
    function getCropEventsByFarm(uint256 farmId) external view returns (uint256[] memory) {
        return farmToCropEvents[farmId];
    }

    /**
     * @dev Get supply chain stages for a product
     * @param productId The product identifier
     * @return Array of stage indices
     */
    function getSupplyChainStages(uint256 productId) external view returns (uint256[] memory) {
        return productToStages[productId];
    }

    /**
     * @dev Get total number of readings
     */
    function getTotalReadings() external view returns (uint256) {
        return readings.length;
    }

    /**
     * @dev Get total number of crop events
     */
    function getTotalCropEvents() external view returns (uint256) {
        return cropEvents.length;
    }

    /**
     * @dev Get total number of supply chain stages
     */
    function getTotalSupplyChainStages() external view returns (uint256) {
        return supplyChainStages.length;
    }

    /**
     * @dev Verify a sensor reading's integrity
     * @param readingId The reading index
     * @return valid Whether the hash matches
     * @return storedHash The stored hash
     */
    function verifyReading(uint256 readingId) 
        external 
        view 
        returns (bool valid, bytes32 storedHash) 
    {
        if (readingId >= readings.length) revert ReadingNotFound();

        SensorReading memory reading = readings[readingId];
        storedHash = reading.dataHash;

        // Note: Can't recompute exact hash without original index for batch submissions
        // This is a simplified verification
        valid = dataHashes[storedHash];
    }

    /**
     * @dev Internal function for basic anomaly detection
     */
    function _checkAnomalies(
        uint256 readingId,
        int16 temperature,
        uint16 soilMoisture,
        uint16 humidity
    ) internal {
        // Example thresholds (configurable in production)
        if (temperature < -100 || temperature > 600) { // -10°C to 60°C
            emit AnomalyDetected(readingId, msg.sender, "EXTREME_TEMPERATURE");
        }
        if (soilMoisture < 50 || soilMoisture > 950) {
            emit AnomalyDetected(readingId, msg.sender, "EXTREME_SOIL_MOISTURE");
        }
        if (humidity > 950) {
            emit AnomalyDetected(readingId, msg.sender, "EXTREME_HUMIDITY");
        }
    }

    /**
     * @dev Pause all contract operations (emergency stop)
     * Only admin can pause
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause contract operations
     * Only admin can unpause
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
}

contracts/Counter.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

contract Counter {
  uint public x;

  event Increment(uint by);

  function inc() public {
    x++;
    emit Increment(1);
  }

  function incBy(uint by) public {
    require(by > 0, "incBy: increment should be positive");
    x += by;
    emit Increment(by);
  }
}

contracts/DifferentialPrivacy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title DifferentialPrivacy
 * @dev Mitigation M8: Local Differential Privacy (LDP) for aggregate statistics
 * @notice Adds calibrated noise to protect individual privacy in aggregates
 */
abstract contract DifferentialPrivacy {
    // Privacy parameters
    uint256 public constant EPSILON = 1e18; // Privacy budget: ε = 1.0 (in wei for precision)
    uint256 public constant SENSITIVITY = 1; // Query sensitivity (Δf = 1 for count queries)

    // Events
    event NoiseAdded(string queryType, int256 trueValue, int256 noisyValue);
    event PrivacyBudgetUsed(address indexed user, uint256 budget);

    // Privacy budget tracking (optional)
    mapping(address => uint256) private _privacyBudgetUsed;
    uint256 public constant MAX_PRIVACY_BUDGET = 10e18; // Maximum ε per user

    /**
     * @dev Adds Laplace noise to a value
     * @param value The true value to protect
     * @param sensitivity The sensitivity of the query
     * @param epsilon The privacy budget (in wei, 1e18 = ε of 1.0)
     * @return Noisy value with differential privacy guarantee
     */
    function _addLaplaceNoise(
        int256 value,
        uint256 sensitivity,
        uint256 epsilon
    ) internal view returns (int256) {
        require(epsilon > 0, "DifferentialPrivacy: epsilon must be positive");

        // Calculate Laplace scale: λ = Δf / ε
        uint256 scale = (sensitivity * 1e18) / epsilon;

        // Sample from Laplace distribution
        int256 noise = _sampleLaplace(scale);

        return value + noise;
    }

    /**
     * @dev Adds Laplace noise using default parameters
     * @param value The true value to protect
     * @return Noisy value with ε = 1.0, Δf = 1
     */
    function _addDefaultNoise(int256 value) internal view returns (int256) {
        return _addLaplaceNoise(value, SENSITIVITY, EPSILON);
    }

    /**
     * @dev Samples from Laplace distribution
     * @param scale The scale parameter (λ)
     * @return Random sample from Laplace(0, scale)
     * @notice Uses block hash as entropy source (not cryptographically secure, but sufficient for DP)
     */
    function _sampleLaplace(uint256 scale) internal view returns (int256) {
        // Generate pseudo-random value using block hash and timestamp
        // WARNING: This is not cryptographically secure but acceptable for differential privacy
        bytes32 randomHash = keccak256(
            abi.encodePacked(
                block.timestamp,
                block.prevrandao,
                msg.sender,
                blockhash(block.number - 1)
            )
        );

        uint256 randomValue = uint256(randomHash);

        // Convert to uniform [0,1) in fixed point (18 decimals)
        uint256 uniform = (randomValue % 1e18);

        // Sample from Laplace using inverse CDF method
        // If U ~ Uniform(0,1), then X = -λ * sign(U - 0.5) * ln(1 - 2|U - 0.5|)
        bool isNegative = uniform < 5e17; // U < 0.5
        uint256 absU = isNegative ? (5e17 - uniform) : (uniform - 5e17);

        // Avoid ln(0) by clamping
        if (absU >= 5e17) {
            absU = 5e17 - 1;
        }

        // Calculate: λ * ln(1 - 2|U - 0.5|)
        // Approximate ln using Taylor series for simplicity
        uint256 x = (2 * absU * 1e18) / 5e17; // 2|U - 0.5|
        int256 lnValue = _approximateLn(1e18 - x);

        int256 noise = -int256((scale * uint256(-lnValue)) / 1e18);

        return isNegative ? -noise : noise;
    }

    /**
     * @dev Approximates natural logarithm using Taylor series
     * @param x Value in fixed point (18 decimals), must be in (0, 1]
     * @return ln(x) in fixed point
     */
    function _approximateLn(uint256 x) internal pure returns (int256) {
        require(x > 0 && x <= 1e18, "DifferentialPrivacy: x out of range");

        if (x == 1e18) return 0;

        // Use Taylor series: ln(1-y) = -y - y²/2 - y³/3 - ...
        uint256 y = 1e18 - x;
        int256 result = 0;
        uint256 term = y;

        for (uint256 i = 1; i <= 10; i++) {
            result -= int256(term / i);
            term = (term * y) / 1e18;
        }

        return result;
    }

    /**
     * @dev Reports a noisy count to protect privacy
     * @param trueCount The actual count
     * @return Noisy count with differential privacy
     */
    function _reportNoisyCount(uint256 trueCount) internal view returns (uint256) {
        int256 noisyValue = _addDefaultNoise(int256(trueCount));

        // Ensure non-negative result
        if (noisyValue < 0) {
            return 0;
        }

        return uint256(noisyValue);
    }

    /**
     * @dev Reports a noisy sum with custom privacy parameters
     * @param trueSum The actual sum
     * @param maxContribution Maximum contribution per user
     * @param privacyBudget Privacy budget to use
     * @return Noisy sum
     */
    function _reportNoisySum(
        uint256 trueSum,
        uint256 maxContribution,
        uint256 privacyBudget
    ) internal view returns (uint256) {
        int256 noisyValue = _addLaplaceNoise(
            int256(trueSum),
            maxContribution,
            privacyBudget
        );

        if (noisyValue < 0) {
            return 0;
        }

        return uint256(noisyValue);
    }

    /**
     * @dev Tracks privacy budget usage for a user
     * @param user Address of the user
     * @param budgetUsed Amount of privacy budget consumed
     */
    function _trackPrivacyBudget(address user, uint256 budgetUsed) internal {
        _privacyBudgetUsed[user] += budgetUsed;
        require(
            _privacyBudgetUsed[user] <= MAX_PRIVACY_BUDGET,
            "DifferentialPrivacy: privacy budget exceeded"
        );
        emit PrivacyBudgetUsed(user, budgetUsed);
    }

    /**
     * @dev Returns remaining privacy budget for a user
     */
    function getRemainingPrivacyBudget(address user) public view returns (uint256) {
        if (_privacyBudgetUsed[user] >= MAX_PRIVACY_BUDGET) {
            return 0;
        }
        return MAX_PRIVACY_BUDGET - _privacyBudgetUsed[user];
    }

    /**
     * @dev Calculates confidence interval for noisy result
     * @param epsilon Privacy budget used
     * @param sensitivity Query sensitivity
     * @return margin Error margin at 95% confidence
     */
    function getConfidenceInterval(
        uint256 epsilon,
        uint256 sensitivity
    ) public pure returns (uint256 margin) {
        // For Laplace mechanism: 95% CI ≈ ±3λ where λ = Δf/ε
        uint256 scale = (sensitivity * 1e18) / epsilon;
        margin = (3 * scale) / 1e18;
    }
}

contracts/InputValidator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title InputValidator
 * @dev Mitigation M4: Input validation and bounded operations
 * @notice Prevents DoS attacks via gas exhaustion and overflow
 */
abstract contract InputValidator {
    // Constants for bounds
    uint256 public constant MAX_BATCH_SIZE = 100;
    uint256 public constant MIN_BATCH_SIZE = 1;
    uint256 public constant MAX_AMOUNT = 1e12 * 1e18; // 1 trillion tokens with 18 decimals
    uint256 public constant MIN_AMOUNT = 1;
    uint256 public constant MAX_STRING_LENGTH = 256;

    // Events
    event ValidationFailed(string reason, uint256 value);

    /**
     * @dev Validates batch size is within bounds
     */
    modifier validBatchSize(uint256 size) {
        require(size >= MIN_BATCH_SIZE, "InputValidator: batch size too small");
        require(size <= MAX_BATCH_SIZE, "InputValidator: batch size too large");
        _;
    }

    /**
     * @dev Validates amount is within bounds
     */
    modifier validAmount(uint256 amount) {
        require(amount >= MIN_AMOUNT, "InputValidator: amount too small");
        require(amount <= MAX_AMOUNT, "InputValidator: amount too large");
        _;
    }

    /**
     * @dev Validates address is not zero
     */
    modifier validAddress(address addr) {
        require(addr != address(0), "InputValidator: zero address");
        _;
    }

    /**
     * @dev Validates string length
     */
    modifier validStringLength(string memory str) {
        require(bytes(str).length <= MAX_STRING_LENGTH, "InputValidator: string too long");
        _;
    }

    /**
     * @dev Validates array has no zero addresses
     */
    function _validateAddressArray(address[] memory addresses) internal pure {
        for (uint256 i = 0; i < addresses.length; i++) {
            require(addresses[i] != address(0), "InputValidator: zero address in array");
        }
    }

    /**
     * @dev Validates amounts array matches addresses array length
     */
    function _validateArrayLengthsMatch(
        address[] memory addresses,
        uint256[] memory amounts
    ) internal pure {
        require(
            addresses.length == amounts.length,
            "InputValidator: array length mismatch"
        );
    }

    /**
     * @dev Validates total amount doesn't overflow
     */
    function _validateTotalAmount(uint256[] memory amounts) internal pure returns (uint256 total) {
        for (uint256 i = 0; i < amounts.length; i++) {
            require(amounts[i] <= MAX_AMOUNT, "InputValidator: amount too large");
            total += amounts[i];
            require(total >= amounts[i], "InputValidator: total overflow");
        }
        require(total <= MAX_AMOUNT, "InputValidator: total too large");
    }

    /**
     * @dev Safe addition with overflow check
     */
    function _safeAdd(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "InputValidator: addition overflow");
        return c;
    }

    /**
     * @dev Safe subtraction with underflow check
     */
    function _safeSub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "InputValidator: subtraction underflow");
        return a - b;
    }

    /**
     * @dev Safe multiplication with overflow check
     */
    function _safeMul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) return 0;
        uint256 c = a * b;
        require(c / a == b, "InputValidator: multiplication overflow");
        return c;
    }

    /**
     * @dev Safe division with zero check
     */
    function _safeDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "InputValidator: division by zero");
        return a / b;
    }

    /**
     * @dev Validates percentage is within 0-100 range
     */
    function _validatePercentage(uint256 percentage) internal pure {
        require(percentage <= 100, "InputValidator: percentage > 100");
    }

    /**
     * @dev Validates timestamp is not in the past
     */
    function _validateFutureTimestamp(uint256 timestamp) internal view {
        require(timestamp > block.timestamp, "InputValidator: timestamp in past");
    }

    /**
     * @dev Validates timestamp is not too far in the future (max 1 year)
     */
    function _validateReasonableTimestamp(uint256 timestamp) internal view {
        require(
            timestamp <= block.timestamp + 365 days,
            "InputValidator: timestamp too far in future"
        );
    }
}

contracts/NonceManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title NonceManager
 * @dev Mitigation M3: Nonce-based replay protection
 * @notice Prevents transaction replay attacks using unique nonces
 */
abstract contract NonceManager {
    // Events
    event NonceUsed(address indexed user, uint256 nonce);

    // Storage
    mapping(address => uint256) public nonces;
    mapping(address => mapping(uint256 => bool)) public usedNonces;

    // Chain ID for cross-chain replay protection
    uint256 public immutable CHAIN_ID;

    constructor() {
        CHAIN_ID = block.chainid;
    }

    /**
     * @dev Returns the current nonce for an address
     */
    function getNonce(address user) public view returns (uint256) {
        return nonces[user];
    }

    /**
     * @dev Checks if a nonce has been used
     */
    function isNonceUsed(address user, uint256 nonce) public view returns (bool) {
        return usedNonces[user][nonce];
    }

    /**
     * @dev Validates and consumes a nonce
     * @notice Reverts if nonce is invalid or already used
     */
    function _useNonce(address user) internal returns (uint256 current) {
        current = nonces[user];
        nonces[user] = current + 1;
        usedNonces[user][current] = true;
        emit NonceUsed(user, current);
    }

    /**
     * @dev Validates a specific nonce without consuming it
     * @notice Used for off-chain signed messages
     */
    function _validateNonce(address user, uint256 nonce) internal view {
        require(nonce == nonces[user], "NonceManager: invalid nonce");
        require(!usedNonces[user][nonce], "NonceManager: nonce already used");
    }

    /**
     * @dev Consumes a specific nonce
     * @notice Used after validating signed messages
     */
    function _consumeNonce(address user, uint256 nonce) internal {
        require(nonce == nonces[user], "NonceManager: invalid nonce");
        require(!usedNonces[user][nonce], "NonceManager: nonce already used");

        nonces[user] = nonce + 1;
        usedNonces[user][nonce] = true;
        emit NonceUsed(user, nonce);
    }

    /**
     * @dev Returns the domain separator for EIP-712
     * @notice Includes chain ID to prevent cross-chain replays
     */
    function _domainSeparator() internal view returns (bytes32) {
        return keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("SecureAssetManager")),
                keccak256(bytes("1")),
                CHAIN_ID,
                address(this)
            )
        );
    }

    /**
     * @dev Recovers signer from a signed message
     * @param nonce The nonce used in the signature
     * @param data The data that was signed
     * @param signature The signature (r, s, v)
     */
    function _recoverSigner(
        uint256 nonce,
        bytes memory data,
        bytes memory signature
    ) internal view returns (address) {
        require(signature.length == 65, "NonceManager: invalid signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 96)))
        }

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                _domainSeparator(),
                keccak256(abi.encode(
                    keccak256("Transaction(uint256 nonce,uint256 chainId,bytes data)"),
                    nonce,
                    CHAIN_ID,
                    keccak256(data)
                ))
            )
        );

        return ecrecover(digest, v, r, s);
    }

    /**
     * @dev Verifies a signed transaction
     * @notice Checks signature and nonce validity
     */
    function _verifySignedTransaction(
        address user,
        uint256 nonce,
        bytes memory data,
        bytes memory signature
    ) internal view {
        _validateNonce(user, nonce);
        address signer = _recoverSigner(nonce, data, signature);
        require(signer == user, "NonceManager: invalid signature");
    }
}

contracts/Pausable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AccessControl.sol";

/**
 * @title Pausable
 * @dev Mitigation M6: Circuit breaker / pause mechanism
 * @notice Allows emergency pause of contract operations
 */
abstract contract Pausable is AccessControl {
    // Events
    event Paused(address indexed account);
    event Unpaused(address indexed account);
    event EmergencyPause(address indexed account, string reason);

    // State
    bool private _paused;
    uint256 private _pauseCount;
    uint256 private _lastPauseTime;
    string private _pauseReason;

    // Emergency contacts
    mapping(address => bool) private _emergencyPausers;

    constructor() {
        _paused = false;
    }

    /**
     * @dev Returns true if the contract is paused
     */
    function paused() public view returns (bool) {
        return _paused;
    }

    /**
     * @dev Returns pause statistics
     */
    function getPauseInfo() public view returns (
        bool isPaused,
        uint256 pauseCount,
        uint256 lastPauseTime,
        string memory reason
    ) {
        return (_paused, _pauseCount, _lastPauseTime, _pauseReason);
    }

    /**
     * @dev Modifier to make a function callable only when not paused
     */
    modifier whenNotPaused() {
        require(!_paused, "Pausable: paused");
        _;
    }

    /**
     * @dev Modifier to make a function callable only when paused
     */
    modifier whenPaused() {
        require(_paused, "Pausable: not paused");
        _;
    }

    /**
     * @dev Pauses the contract
     * @notice Requires PAUSER_ROLE
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause("Manual pause by admin");
    }

    /**
     * @dev Pauses the contract with a reason
     * @notice Requires PAUSER_ROLE
     */
    function pauseWithReason(string memory reason) public onlyRole(PAUSER_ROLE) {
        require(bytes(reason).length > 0, "Pausable: reason required");
        _pause(reason);
    }

    /**
     * @dev Emergency pause - can be called by designated emergency pausers
     * @notice Use in critical situations (active attack, critical bug)
     */
    function emergencyPause(string memory reason) public {
        require(
            _emergencyPausers[msg.sender] || hasRole(PAUSER_ROLE, msg.sender),
            "Pausable: not authorized for emergency pause"
        );
        require(bytes(reason).length > 0, "Pausable: reason required");

        _pause(reason);
        emit EmergencyPause(msg.sender, reason);
    }

    /**
     * @dev Unpauses the contract
     * @notice Requires PAUSER_ROLE
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        require(_paused, "Pausable: not paused");
        _paused = false;
        _pauseReason = "";
        emit Unpaused(msg.sender);
    }

    /**
     * @dev Internal function to pause
     */
    function _pause(string memory reason) internal whenNotPaused {
        _paused = true;
        _pauseCount += 1;
        _lastPauseTime = block.timestamp;
        _pauseReason = reason;
        emit Paused(msg.sender);
    }

    /**
     * @dev Adds an emergency pauser
     * @notice Only admin can add emergency pausers
     */
    function addEmergencyPauser(address account) public onlyRole(ADMIN_ROLE) {
        require(account != address(0), "Pausable: zero address");
        _emergencyPausers[account] = true;
    }

    /**
     * @dev Removes an emergency pauser
     */
    function removeEmergencyPauser(address account) public onlyRole(ADMIN_ROLE) {
        _emergencyPausers[account] = false;
    }

    /**
     * @dev Checks if an address is an emergency pauser
     */
    function isEmergencyPauser(address account) public view returns (bool) {
        return _emergencyPausers[account];
    }

    /**
     * @dev Auto-unpause after specified time (safety feature)
     * @notice Prevents indefinite pause
     */
    function _checkAutoPause() internal view {
        if (_paused && block.timestamp > _lastPauseTime + 7 days) {
            // Consider implementing auto-unpause logic or alerting
            // For safety, we don't auto-unpause but this can be monitored
        }
    }
}

contracts/RateLimiter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title RateLimiter
 * @dev Mitigation M5: Rate limiting using token bucket algorithm
 * @notice Prevents DoS attacks via transaction spam
 */
abstract contract RateLimiter {
    // Rate limit configuration
    uint256 public constant MAX_TOKENS = 10; // Max transactions in bucket
    uint256 public constant REFILL_RATE = 1; // Tokens per time unit
    uint256 public constant TIME_UNIT = 1 minutes;

    // Events
    event RateLimitExceeded(address indexed user, uint256 timestamp);
    event RateLimitReset(address indexed user);

    // Storage
    struct RateLimit {
        uint256 tokens;
        uint256 lastRefill;
        uint256 totalRequests;
    }

    mapping(address => RateLimit) private _rateLimits;

    // Global rate limit for contract
    uint256 private _globalTokens = 1000;
    uint256 private _globalLastRefill;
    uint256 public constant GLOBAL_MAX_TOKENS = 1000;
    uint256 public constant GLOBAL_REFILL_RATE = 10;

    constructor() {
        _globalLastRefill = block.timestamp;
    }

    /**
     * @dev Modifier to enforce rate limiting
     */
    modifier rateLimited() {
        _checkRateLimit(msg.sender);
        _;
    }

    /**
     * @dev Modifier to enforce global rate limiting
     */
    modifier globalRateLimited() {
        _checkGlobalRateLimit();
        _;
    }

    /**
     * @dev Checks and updates rate limit for a user
     */
    function _checkRateLimit(address user) internal {
        RateLimit storage limit = _rateLimits[user];

        // Initialize if first request
        if (limit.lastRefill == 0) {
            limit.tokens = MAX_TOKENS;
            limit.lastRefill = block.timestamp;
        }

        // Calculate tokens to add based on elapsed time
        uint256 elapsed = block.timestamp - limit.lastRefill;
        uint256 tokensToAdd = (elapsed / TIME_UNIT) * REFILL_RATE;

        if (tokensToAdd > 0) {
            limit.tokens = _min(limit.tokens + tokensToAdd, MAX_TOKENS);
            limit.lastRefill = block.timestamp;
        }

        // Check if user has tokens available
        if (limit.tokens < 1) {
            emit RateLimitExceeded(user, block.timestamp);
            revert("RateLimiter: rate limit exceeded");
        }

        // Consume a token
        limit.tokens -= 1;
        limit.totalRequests += 1;
    }

    /**
     * @dev Checks and updates global rate limit
     */
    function _checkGlobalRateLimit() internal {
        uint256 elapsed = block.timestamp - _globalLastRefill;
        uint256 tokensToAdd = (elapsed / TIME_UNIT) * GLOBAL_REFILL_RATE;

        if (tokensToAdd > 0) {
            _globalTokens = _min(_globalTokens + tokensToAdd, GLOBAL_MAX_TOKENS);
            _globalLastRefill = block.timestamp;
        }

        require(_globalTokens >= 1, "RateLimiter: global rate limit exceeded");
        _globalTokens -= 1;
    }

    /**
     * @dev Returns the current rate limit status for a user
     */
    function getRateLimitStatus(address user) public view returns (
        uint256 availableTokens,
        uint256 totalRequests,
        uint256 nextRefillTime
    ) {
        RateLimit storage limit = _rateLimits[user];

        if (limit.lastRefill == 0) {
            return (MAX_TOKENS, 0, block.timestamp);
        }

        uint256 elapsed = block.timestamp - limit.lastRefill;
        uint256 tokensToAdd = (elapsed / TIME_UNIT) * REFILL_RATE;
        availableTokens = _min(limit.tokens + tokensToAdd, MAX_TOKENS);
        totalRequests = limit.totalRequests;

        // Calculate next refill time
        uint256 tokensUntilFull = MAX_TOKENS - limit.tokens;
        uint256 timeToFull = (tokensUntilFull * TIME_UNIT) / REFILL_RATE;
        nextRefillTime = limit.lastRefill + timeToFull;
    }

    /**
     * @dev Returns global rate limit status
     */
    function getGlobalRateLimitStatus() public view returns (
        uint256 availableTokens,
        uint256 nextRefillTime
    ) {
        uint256 elapsed = block.timestamp - _globalLastRefill;
        uint256 tokensToAdd = (elapsed / TIME_UNIT) * GLOBAL_REFILL_RATE;
        availableTokens = _min(_globalTokens + tokensToAdd, GLOBAL_MAX_TOKENS);

        uint256 tokensUntilFull = GLOBAL_MAX_TOKENS - _globalTokens;
        uint256 timeToFull = (tokensUntilFull * TIME_UNIT) / GLOBAL_REFILL_RATE;
        nextRefillTime = _globalLastRefill + timeToFull;
    }

    /**
     * @dev Admin function to reset user rate limit (emergency use)
     */
    function _resetRateLimit(address user) internal {
        RateLimit storage limit = _rateLimits[user];
        limit.tokens = MAX_TOKENS;
        limit.lastRefill = block.timestamp;
        emit RateLimitReset(user);
    }

    /**
     * @dev Returns minimum of two values
     */
    function _min(uint256 a, uint256 b) private pure returns (uint256) {
        return a < b ? a : b;
    }

    /**
     * @dev Checks if user would be rate limited (view function)
     */
    function wouldBeRateLimited(address user) public view returns (bool) {
        RateLimit storage limit = _rateLimits[user];

        if (limit.lastRefill == 0) {
            return false; // First request is never limited
        }

        uint256 elapsed = block.timestamp - limit.lastRefill;
        uint256 tokensToAdd = (elapsed / TIME_UNIT) * REFILL_RATE;
        uint256 availableTokens = _min(limit.tokens + tokensToAdd, MAX_TOKENS);

        return availableTokens < 1;
    }
}

